let appState={recipes:null,currentRecipe:null,refresh:function(){axios.get("api/recipes/list").then(function(n){appState.recipes=n.data.items})}};appState.refresh();let recipeTable=new Vue({el:"#recipe-table",data:appState,methods:{selectRecipe:function(n){appState.currentRecipe=n}}}),recipeEditor=new Vue({el:"#recipe-form",data:appState,methods:{clearCurrent:function(){appState.currentRecipe=null}}});